clear ; close all; clc;U = [ 3 -1 ;      1  3 ];D = [ 2  0 ;      0  1 ];      U(:,1) ./= norm(U(:,1));U(:,2) ./= norm(U(:,2));A = U * D * U';plot([0, U(1,1)], [0, U(2,1)], 'r-', [0, U(1,2)], [0, U(2,2)], 'r-');axis("square", "equal");hold on;#plot([0, A(1,1)], [0, A(2,1)], 'g-', [0, A(1,2)], [0, A(2,2)], 'g-');plot([0, A(1,1)], [0, A(2,1)], 'g-', [0, A(1,2)], [0, A(2,2)], 'g-');plot([0, 0], [-3, 5], 'b-', [7, -5], [0, 0], 'b-');